import React from "react";
import "./left-panel.css";

const LeftPanel = ({ inputHandler, input, setSignFlag, signFlag }) => {
  const btns = [
    ["7", "8", "9"],
    ["4", "5", "6"],
    ["1", "2", "3"],
    ["0", ".", "AC"],
  ];
  const onBtn = (e) => {
    const { target } = e;
    setSignFlag(true);
    if (target.textContent === "AC") {
      inputHandler("0");
      return;
    }
    if (target.textContent === ".") {
      if (signFlag) {
        inputHandler(input + target.textContent);
      }
    }
    if (input === "0") {
      inputHandler(target.textContent);
    } else {
      inputHandler(input + target.textContent);
    }
  };
  return (
    <div className="leftPanel">
      {btns.map((bt, i) => (
        <div key={i} className="numbers">
          {bt.map((b) => (
            <div key={b} onClick={onBtn}>
              {b}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default LeftPanel;
