import { useState } from "react";
import "./App.css";
import Calculator from "./components/calculator";

function App() {
  const [input, setInput] = useState("0");
  const [signFlag, setSignFlag] = useState(true);
  const [sign, setSign] = useState("");
  const [commaFlag, setCommaFlag] = useState([true, true]);
  const inputHandler = (m) => {
    setInput(m);
  };
  const equalHandler = (e) => {
    setInput("EQUAL");
    console.log(input);
    console.log(sign);
    let arr;
    let res;
    switch (sign) {
      case "+":
        arr = input.split("+");
        res = arr[0] + arr[1];
        break;
      case "-":
        arr = input.split("-");
        res = arr[0] - arr[1];
        break;
      case "*":
        arr = input.split("*");
        res = arr[0] * arr[1];
        break;
      default:
        return;
    }
  };
  return (
    <Calculator
      inputHandler={inputHandler}
      input={input}
      signFlag={signFlag}
      setSignFlag={setSignFlag}
      equalHandler={equalHandler}
      setSign={setSign}
    />
  );
}

export default App;
