import React from "react";

const Operators = ({ inputHandler, input, signFlag, setSignFlag }) => {
  const operatorsSigns = ["+", "-", "*", "/"];
  const onOpBtn = (e) => {
    const { target } = e;
    setSignFlag(false);
    if (signFlag) {
      inputHandler(input + target.textContent);
    }
  };
  return (
    <div className="operators">
      {operatorsSigns.map((op) => (
        <div key={op} onClick={onOpBtn}>
          {op}
        </div>
      ))}
    </div>
  );
};

export default Operators;
