import { useState } from "react";
import "./App.css";
import Calculator from "./components/calculator";

function App() {
  const [input, setInput] = useState("0");
  const [signFlag, setSignFlag] = useState(true);
  const [sign, setSign] = useState("");
  const [commaFlag, setCommaFlag] = useState([true, true]);
  const [startAgain, setStartAgain] = useState(false);
  const step = (n) => 10 ** (n.toString().split(".")[1] || "").length;

  const calc = (num1, num2, oper) => {
    const kf = Math.max(...[step(num1), step(num2)]);
    let res;
    console.log([step(num1), step(num2)]);
    console.log(kf);
    switch (oper) {
      case "+":
        res = (Number(num1) * kf + Number(num2) * kf) / kf;
        break;
      case "-":
        res = (Number(num1) * kf - Number(num2) * kf) / kf;
        break;
      case "*":
        res = (Number(num1) * kf * Number(num2) * kf) / kf;
        break;
      case "/":
        res = (((Number(num1) * kf) / Number(num2)) * kf) / kf;
        break;
      default:
        res = "0";
    }
    console.log("RES: ", res);
    return res;
  };

  const inputHandler = (m) => {
    setInput(m);
  };
  const resetHandler = () => {
    setInput("0");
    setSignFlag(true);
    setSign("");
    setCommaFlag([true, true]);
    setStartAgain(false);
  };
  const equalHandler = () => {
    let arr;
    if (sign) {
      arr = input.split(sign);
      if (arr[1] === "") arr[1] = arr[0];
      console.log(arr);
    }
    const res = calc(arr[0], arr[1], sign);

    res ? setInput(res) : setInput("Ошибка ввода!");
    setCommaFlag(true);
    setSignFlag(true);
    setStartAgain(true);
  };
  return (
    <Calculator
      inputHandler={inputHandler}
      input={input}
      signFlag={signFlag}
      setSignFlag={setSignFlag}
      equalHandler={equalHandler}
      setSign={setSign}
      commaFlag={commaFlag}
      setCommaFlag={setCommaFlag}
      startAgain={startAgain}
      setStartAgain={setStartAgain}
      resetHandler={resetHandler}
    />
  );
}

export default App;
