import { useState } from "react";
import "./App.css";
import Calculator from "./components/calculator";

function App() {
  const [input, setInput] = useState("0");
  const [signFlag, setSignFlag] = useState(true);
  const [sign, setSign] = useState("");
  const [commaFlag, setCommaFlag] = useState([true, true]);
  const [startAgain, setStartAgain] = useState(false);
  const step = (n) => n.toString().split(".")[1].length;
  console.log((0.054892 * 10 ** step(0.054892)) / 10 ** step(0.054892));
  const inputHandler = (m) => {
    setInput(m);
  };
  const equalHandler = () => {
    let arr;
    let res;
    switch (sign) {
      case "+":
        arr = input.split("+");
        res = Number(arr[0]) + Number(arr[1]);
        break;
      case "-":
        arr = input.split("-");
        res = Number(arr[0]) - Number(arr[1]);
        break;
      case "*":
        arr = input.split("*");
        res = Number(arr[0]) * Number(arr[1]);
        break;
      case "/":
        arr = input.split("/");
        res = Number(arr[0]) / Number(arr[1]);
        break;
      default:
        res = input;
    }
    res ? setInput(res) : setInput("Ошибка ввода!");
    setCommaFlag(true);
    setSignFlag(true);
    setStartAgain(true);
  };
  return (
    <Calculator
      inputHandler={inputHandler}
      input={input}
      signFlag={signFlag}
      setSignFlag={setSignFlag}
      equalHandler={equalHandler}
      setSign={setSign}
      commaFlag={commaFlag}
      setCommaFlag={setCommaFlag}
      startAgain={startAgain}
      setStartAgain={setStartAgain}
    />
  );
}

export default App;
