import { useEffect, useState } from "react";
import "./App.css";
import Calculator from "./components/calculator";
import { calc } from "./utils";

function App() {
  const [input, setInput] = useState("0");
  const [signFlag, setSignFlag] = useState(true);
  const [sign, setSign] = useState("");
  const [commaFlag, setCommaFlag] = useState([true, true]);
  const [startAgain, setStartAgain] = useState(false);

  useEffect(() => {
    console.log("============STATE=============");
    console.log("INPUT: ", input);
    console.log("signFlag: ", signFlag);
    console.log("sign: ", sign);
    console.log("commaFlag: ", commaFlag);
    console.log("startAgain: ", startAgain);
  }, [input]);

  const inputHandler = (m) => {
    setInput(m);
  };

  const resetHandler = () => {
    setInput("0");
    setSignFlag(true);
    setSign("");
    setCommaFlag([true, true]);
    setStartAgain(false);
  };

  const onBtn = (e) => {
    const { target } = e;

    if (target.textContent === "AC") {
      resetHandler();
      return;
    } else if (target.textContent === ".") {
      if (commaFlag || !startAgain) {
        inputHandler(input + target.textContent);
        setCommaFlag(false);
      }
    } else {
      if (input === "0" || startAgain) {
        inputHandler(target.textContent);
        setStartAgain(false);
      } else {
        inputHandler(input + target.textContent);
      }
    }
  };

  const equalHandler = () => {
    try {
      let arr;
      if (sign) {
        arr = input.split(sign);
        if (arr[1] === "") arr[1] = arr[0];
        console.log(arr);
      } else {
        return;
      }
      const res = calc(arr[0], arr[1], sign);

      res ? setInput(res) : setInput("Ой!");
      setCommaFlag(true);
      setSignFlag(true);
      setStartAgain(true);
      setSign("");
    } catch (err) {
      console.log("ERROR: ", err);
      resetHandler();
      return;
    }
  };
  return (
    <Calculator
      inputHandler={inputHandler}
      input={input}
      signFlag={signFlag}
      setSignFlag={setSignFlag}
      equalHandler={equalHandler}
      setSign={setSign}
      commaFlag={commaFlag}
      setCommaFlag={setCommaFlag}
      startAgain={startAgain}
      setStartAgain={setStartAgain}
      resetHandler={resetHandler}
      onBtn={onBtn}
    />
  );
}

export default App;
