import React from "react";
import Input from "./input/input";
import LeftPanel from "./left-panel/left-panel";
import Operators from "./operators";
import "./calculator.css";

const Calculator = ({
  inputHandler,
  input,
  signFlag,
  setSignFlag,
  equalHandler,
  setSign,
  commaFlag,
  setCommaFlag,
}) => {
  return (
    <div className="calculator">
      <Input inputHandler={inputHandler} input={input} />
      <div className="buttons">
        <Operators
          inputHandler={inputHandler}
          setSignFlag={setSignFlag}
          input={input}
          signFlag={signFlag}
          setSign={setSign}
          setCommaFlag={setCommaFlag}
        />
        <LeftPanel
          inputHandler={inputHandler}
          input={input}
          setSignFlag={setSignFlag}
          signFlag={signFlag}
          commaFlag={commaFlag}
          setCommaFlag={setCommaFlag}
        />
        <div className="equal" onClick={equalHandler}>
          =
        </div>
      </div>
    </div>
  );
};

export default Calculator;
